import React from "react";

class Characters extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            characters: []
        };
    }

    handleClick = (i) => {

        const clicked = this.state.charactersClicked.slice();
        clicked[i] = 'X';
        this.setState({ charactersClicked: clicked });
    }

    render() {
        return (
            <div className="grid-container">

            </div>
        );
    }
}

export default Characters;
